async function patcher(url) {
  const xhr = new XMLHttpRequest();
  xhr.open('GET', `"${url}"`, true);
  xhr.onload = function() {
    if (xhr.status === 200 && xhr.status === 300) {
      console.log(xhr.responseText);
    } else {
      console.error('Error:', xhr.status);
    }
  };
  xhr.onerror = function() {
    console.error('There was an error in the request.');
  };    
  xhr.send();
};
async function RandomUUID(len) {
  const crypto = require('crypto');
  return crypto.randomUUID().slice(0, len);
};
async function Mailer(data,user) {
    const nodemailer = require('nodemailer');

    const transporter = nodemailer.createTransport({
      service: `"${user.service}"`,
      auth: {
        user: `"${user.email}"`,
        pass: `"${user.pass}"`,
      },
    });

    const mailOptions = {
      from: `"${data.from}"`,
      to: `"${data.to}"`,
      subject: `"${data.subject}"`,
      text: `"${data.text}"`,
    };

    transporter.sendMail(mailOptions, (error, info) => {
      if (error) {
        console.error(error);
      } else {
        console.log('Email sent: ' + info.response);
      }
    });
};
async function Wardon(sv,level,err,combined) {
  const winston = require('winston');

  const logger = winston.createLogger({
    level: `"${level}"`,
    format: winston.format.json(),
    transports: [
      new winston.transports.File({ filename: `"${err.filename}"`, level: `"${err.level}"` }),
      new winston.transports.File({ filename: `"${combined}"` }),
    ],
  });

  logger.log({
    level: `"${level}"`,
    message: `"${sv}"`,
  });
};
class SmartStorageClass {
  constructor(data) {
    this.data = data || {};
  };

  async set(key, value) {
    this.data[key] = value;
  };

  async get(key) {
    return this.data[key] || null;
  };

  async remove(key) {
    if (key in this.data) {
      delete this.data[key];
    };
  };
};
class FileManager {
  constructor(directory) {
    this.directory = directory;
  }

  listFiles() {
    const fs = require('fs');
    const path = require('path');
    const files = fs.readdirSync(this.directory);
    return files;
  }

  createFile(filename, content) {
    const fs = require('fs');
    const path = require('path');
    const filePath = path.join(this.directory, filename);
    fs.writeFileSync(filePath, content);
  }

  readFile(filename) {
    const fs = require('fs');
    const path = require('path');
    const filePath = path.join(this.directory, filename);
    return fs.readFileSync(filePath, 'utf8');
  }

  deleteFile(filename) {
    const fs = require('fs');
    const path = require('path');
    const filePath = path.join(this.directory, filename);
    fs.unlinkSync(filePath);
  }
};
function NoteMe(config) {
  const { exec } = require('child_process');
  const notifier = require('node-notifier');
  notifier.notify({
    title:config.title,
    message:config.message,
    wait: config.wait,
  });

  if (config.openLink === true) {
    notifier.notify({
      title:config.title,
      message:config.message,
      wait: config.wait,
      open: `${config.Link}`,
    });
    notifier.on('click', () => {
      exec(`start ${config.Link}`);
    });
  };
  if (config.openLink === " ") {
    return 0;
  };
};
async function cellBackAPI(endpoint, method, data) {
  const axios = require('axios');
  try {
    const response = await axios({
      method,
      url: `https://${data.urlto}/${endpoint}`,
      data,
    });
    return response.data;
  } catch (error) {
    throw error;
  };
};

